import "./index.css";

const FilmRow = ({ film,isSelectFave, handleSelectDetail, handleSelectFave }) => {
  return (
    <>
      <button className="FilmRow" onClick={() => handleSelectDetail(film)}>
        <img
          src={`https://image.tmdb.org/t/p/w780${film.poster_path}`}
          alt="{film.title} film poster"
        />
        <div className="film-summary">
          <h3>{film.title}</h3>
          <p>{film.release_date}</p>
        </div>
        <button className="fave" onClick={() => handleSelectFave(film)}>
          <span className="material-icons">add_to_queue</span>
        </button>
      </button>
    </>
  );
};
export default FilmRow;
