import './index.css'

const FilmRow = ({ posterPath, title, year, handleSelectDetail }) => {
  return (
    <>
      <button className="FilmRow" onClick={this.handleSelectDetail}>
        <img
          src={`https://image.tmdb.org/t/p/w780${posterPath}`}
          alt="{film title} film poster"
        />
        <div className="film-summary">
          <h3>{title}</h3>
          <p>{year}</p>
        </div>
        <button className="fave">
          <span className="material-icons">add_to_queue</span>
        </button>
      </button>
    </>
  );
};
export default FilmRow;
