import React, { useState } from "react";
import FilmDetail from "./FilmDetail";
import "./FilmLibrary.css";
import { TMDB } from "./TMDB";
import FilmRow from "./components/FilmRow";

const FilmLibrary = () => {
  const [selectedFilm, setSelectedFilm] = useState(null);

  const handleSelectDetail = (film) => {
    // setSelectedFilm(film)
    console.log(film);
  }

  return (
    <div className="FilmLibrary">
      <div className="film-list">
        <h1 className="section-title">FILMS</h1>
        <div className="film-list-filters">
          <button className="film-list-filter is-active">
            ALL
            <span className="section-count">{TMDB.length}</span>
          </button>
          <button className="film-list-filter">
            FAVES
            <span className="section-count">1</span>
          </button>
        </div>
        {TMDB.map((film) => {
          return (
            <FilmRow
            id = {film.id}
              posterPath={film.poster_path}
              title={film.title}
              year={film.release_date}
              handleSelectDetail={() => this.handleSelectDetail(film.id)}
            />
          );
        })}
      </div>

      <div className="film-details">
        <h1 className="section-title">DETAILS</h1>
        <FilmDetail />
      </div>
    </div>
  );
}

export default FilmLibrary;
